import React, { useState } from 'react';
import { Container, Button, Alert } from 'react-bootstrap';
import { Controlled as CodeMirror } from '@codemirror/react';
import 'codemirror/lib/codemirror.css';
import 'codemirror/mode/clike/clike';

const CodeEditor = () => {
    const [code, setCode] = useState('// Write your C++ code here\n');
    const [output, setOutput] = useState('');
    const [error, setError] = useState('');

    const runCode = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/ask', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ query: `code:${code}` })
            });
            const data = await response.json();
            setOutput(data.response);
            setError('');
        } catch (err) {
            setError('Error running code');
            setOutput('');
        }
    };

    return (
        <Container>
            <h2>Code Editor</h2>
            <CodeMirror
                value={code}
                options={{ mode: 'text/x-c++src', lineNumbers: true }}
                onBeforeChange={(editor, data, value) => setCode(value)}
            />
            <Button className="mt-3" onClick={runCode}>Run Code</Button>
            {output && <Alert variant="success" className="mt-3">{output}</Alert>}
            {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
        </Container>
    );
};

export default CodeEditor;
