import React, { useState } from 'react';
import axios from 'axios';
import './AIAssistantPage.css';

function AIAssistantPage() {
  const [input, setInput] = useState('');
  const [suggestions, setSuggestions] = useState('');
  const [debuggingAdvice, setDebuggingAdvice] = useState('');

  const getSuggestions = () => {
    axios.post('http://127.0.0.1:5000/api/ai/suggestions', { code: input })
      .then(response => setSuggestions(response.data.suggestions))
      .catch(error => console.error('Error fetching suggestions:', error));
  };

  const debugCode = () => {
    axios.post('http://127.0.0.1:5000/api/ai/debug', { code: input })
      .then(response => setDebuggingAdvice(response.data.debuggingAdvice))
      .catch(error => console.error('Error debugging code:', error));
  };

  return (
    <div className="ai-assistant-container">
      <h1>AI Assistant</h1>
      <textarea
        value={input}
        onChange={e => setInput(e.target.value)}
        placeholder="Paste your code here"
      />
      <button onClick={getSuggestions}>Get Suggestions</button>
      <button onClick={debugCode}>Debug Code</button>
      <div>
        <h2>Suggestions</h2>
        <pre>{suggestions}</pre>
      </div>
      <div>
        <h2>Debugging Advice</h2>
        <pre>{debuggingAdvice}</pre>
      </div>
    </div>
  );
}

export default AIAssistantPage;
