import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './QuizPage.css';

function QuizPage() {
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [quizzes, setQuizzes] = useState([]);
  const [currentQuiz, setCurrentQuiz] = useState(0);
  const [score, setScore] = useState(0);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/tutorials')
      .then(response => setTopics(response.data))
      .catch(error => console.error('Error fetching topics:', error));
  }, []);

  const handleTopicClick = (topic) => {
    axios.get(`http://127.0.0.1:5000/api/quizzes/${topic}`)
      .then(response => {
        setSelectedTopic(topic);
        setQuizzes(response.data);
        setCurrentQuiz(0);
        setScore(0);
      })
      .catch(error => console.error('Error fetching quizzes:', error));
  };

  const handleAnswer = (answer) => {
    if (answer === quizzes[currentQuiz].answer) {
      setScore(score + 1);
    }
    const nextQuiz = currentQuiz + 1;
    if (nextQuiz < quizzes.length) {
      setCurrentQuiz(nextQuiz);
    } else {
      alert(`Quiz completed! Your score: ${score + 1}/${quizzes.length}`);
    }
  };

  return (
    <div className="quiz-container">
      <div className="sidebar">
        <ul>
          {topics.map(topic => (
            <li key={topic.id} onClick={() => handleTopicClick(topic.title.toLowerCase())}>
              {topic.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="content">
        {selectedTopic ? (
          <div>
            <h2>{selectedTopic} Quiz</h2>
            {quizzes.length > 0 ? (
              <div>
                <p>{quizzes[currentQuiz].question}</p>
                <ul>
                  {quizzes[currentQuiz].options.map((option, index) => (
                    <li key={index} onClick={() => handleAnswer(option)}>
                      {option}
                    </li>
                  ))}
                </ul>
              </div>
            ) : (
              <p>No quizzes available for this topic.</p>
            )}
          </div>
        ) : (
          <p>Please select a topic from the sidebar.</p>
        )}
      </div>
    </div>
  );
}

export default QuizPage;
