import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './QuizPage.css';

function QuizPage() {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [answers, setAnswers] = useState({});
  const [results, setResults] = useState({});

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/quizzes')
      .then(response => setQuizzes(response.data))
      .catch(error => console.error('Error fetching quizzes:', error));
  }, []);

  const handleQuizClick = (quizId) => {
    axios.get(`http://127.0.0.1:5000/api/quizzes/${quizId}`)
      .then(response => {
        setSelectedQuiz(response.data);
        setAnswers({});
        setResults({});
      })
      .catch(error => console.error('Error fetching quiz:', error));
  };

  const handleAnswerChange = (questionIndex, answer) => {
    setAnswers({
      ...answers,
      [questionIndex]: answer
    });
  };

  const handleSubmit = () => {
    const newResults = {};
    selectedQuiz.questions.forEach((question, index) => {
      newResults[index] = question.answer === answers[index];
    });
    setResults(newResults);
  };

  return (
    <div className="quiz-container">
      <div className="sidebar">
        <ul>
          {quizzes.map(quiz => (
            <li key={quiz.id} onClick={() => handleQuizClick(quiz.id)}>
              {quiz.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="content">
        {selectedQuiz ? (
          <div>
            <h2>{selectedQuiz.title}</h2>
            {selectedQuiz.questions.map((question, index) => (
              <div key={index}>
                <p>{question.question}</p>
                {question.options.map((option, i) => (
                  <label key={i}>
                    <input
                      type="radio"
                      name={`question-${index}`}
                      value={option}
                      onChange={() => handleAnswerChange(index, option)}
                    />
                    {option}
                  </label>
                ))}
                {results.hasOwnProperty(index) && (
                  <p>{results[index] ? 'Correct!' : 'Incorrect'}</p>
                )}
              </div>
            ))}
            <button onClick={handleSubmit}>Submit Answers</button>
          </div>
        ) : (
          <p>Please select a quiz from the sidebar.</p>
        )}
      </div>
    </div>
  );
}

export default QuizPage;
