import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TutorialPage.css';

function TutorialPage() {
  const [tutorials, setTutorials] = useState([]);
  const [selectedTutorial, setSelectedTutorial] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/tutorials')
      .then(response => setTutorials(response.data))
      .catch(error => console.error('Error fetching tutorials:', error));
  }, []);

  const handleTutorialClick = (tutorialId) => {
    axios.get(`http://127.0.0.1:5000/api/tutorials/${tutorialId}`)
      .then(response => setSelectedTutorial(response.data))
      .catch(error => console.error('Error fetching tutorial:', error));
  };

  return (
    <div className="tutorial-container">
      <div className="sidebar">
        <ul>
          {tutorials.map(tutorial => (
            <li key={tutorial.id} onClick={() => handleTutorialClick(tutorial.id)}>
              {tutorial.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="content">
        {selectedTutorial ? (
          <div>
            <h2>{selectedTutorial.title}</h2>
            <p>{selectedTutorial.content}</p>
          </div>
        ) : (
          <p>Please select a tutorial from the sidebar.</p>
        )}
      </div>
    </div>
  );
}

export default TutorialPage;
